import React, { useEffect, useState } from 'react'
import './header.css'
import logo from '../../assets/data/logo';
import { Link } from 'react-router-dom';

export default function Header() {

  const [scroll,setScroll] = useState(false);
  useEffect(()=>{
    window.addEventListener("scroll",()=>{
      setScroll(window.scrollY > 100);
    });
    return () => {
      setScroll();
    };
  },[]);

  return (
    <div>
      {/* ${scroll ? 'isfixed' : ''} 是一个条件表达式，它的含义是：如果 scroll 是 true，
      则将类名设置为 isfixed，否则为空字符串。 */}
      <header id="header_main" className={`header js-header ${scroll ? 'isfixed' : ''}`}>
        <div className="container">
          <div className="row">
            <div className="col-12">
              <div className="header_body d-flex justify-content-between">

                <div className="header_logo">
                  <Link to='/'>
                    <img src={logo} alt="" id='site-logo'/>
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </header>
    </div>
  )
}
